package lesson;

public class Lesson05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		double e = 1.0;
		while (1.0 + e != 1.0){
			e = e /2.0;
		}
		
		System.out.println(2.0 * e);
		float f = 1;
		while (1+ f != 1) {
			f = f /2f;
		}
		System.out.println(2 * f);
	}

}
